import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: 'app.component.html'
})

export class AppComponent {
  titulo: string = 'Contador App';

  numero: number = 0;
}
<h1>{{ titulo }}</h1>

<button (click)="numero = numero +1;"> + 1 </button>

<span> {{ numero }} </span>

<button (click)="numero = numero - 1;"> - 1 </button>
